require 'rails_helper'

RSpec.describe DownloadsController, type: :controller do
  describe 'GET #show' do
    let(:headers) do
      {
        'CONTENT_TYPE' => 'application/json',
        'x-access-token-v2' => jwt
      }
    end
    let(:payload_query_string) do
      json = query_string_payload.to_json
      base64 = Base64.strict_encode64(json)
    end
    let(:user_id) { 'abc' }
    let(:service_slug) { 'service-slug' }
    let(:jwt) { JWT.encode({sub: user_id, iat: Time.current.to_i}, private_key, 'RS256') }
    let(:encoded_private_key) { '' }
    let(:private_key) { OpenSSL::PKey::RSA.new(Base64.strict_decode64(encoded_private_key)) }
    let(:encoded_public_key) { '' }
    let(:public_key) { OpenSSL::PKey::RSA.new(Base64.strict_decode64(encoded_public_key)) }
    let(:fake_service) { double(:service) }

    before do
      allow(ServiceTokenService).to receive(:new).with(service_slug: service_slug).and_return(fake_service)
      allow(fake_service).to receive(:public_key).and_return(public_key)
      request.headers.merge!(headers)
    end

    context 'when there are missing paramters' do
      context 'missing payload' do
        it 'returns error' do
          url_params = { service_slug: 'service-slug', user_id: user_id, fingerprint_with_prefix: '28d-fingerprint' }
          get :show, params: url_params
          expect(response).to be_forbidden
        end
      end

      context 'missing encrypted_user_id_and_token' do
        let(:payload_query_string) do
          json = {}.to_json
          base64 = Base64.strict_encode64(json)
        end

        it 'returns error' do
          url_params = { service_slug: 'service-slug',
                         user_id: user_id,
                         fingerprint_with_prefix: '28d-fingerprint',
                         payload: payload_query_string }
          get :show, params: url_params
          expect(response).to be_forbidden
        end
      end
    end
  end
end
